/*
  Placeholder for JSZip library (minified). To make the app fully offline, replace this file
  with a full build of JSZip (e.g., https://cdnjs.cloudflare.com/ajax/libs/jszip/3.10.1/jszip.min.js).

  For now, this is a no-op that ensures the file exists in the repo and can be cached by the
  service worker. If JSZip is not present in the window scope at runtime, the app will try
  to load the library from CDN.
*/
(function (root) {
  if (!root) return;
  if (!root.JSZip) {
    // Minimal stub to avoid runtime errors; ODT export will check JSZip availability and abort if missing.
    root.JSZip = function() {
      console.warn('JSZip stub used: please replace js/vendor/jszip.min.js with the full JSZip library for offline ODT export.');
      this.files = {};
    };
    root.JSZip.prototype.file = function(name, content) {
      this.files[name] = content;
    };
    root.JSZip.prototype.folder = function() { return this; };
    root.JSZip.prototype.generateAsync = function(options) {
      return Promise.reject(new Error('JSZip is a stub. Replace with full library to create archives.'));
    };
  }
})(typeof window !== 'undefined' ? window : (typeof global !== 'undefined' ? global : this));
